package DayOne;

public class Char {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'a';
		System.out.println(ch);

		char num = 70;
		System.out.println(num);

		char UniChar = '\u00A7';
		System.out.println(UniChar);

		char unicode = '\u20AC';
		System.out.println(unicode);

		int asci = 'F';
		System.out.println(asci);
			

	}

}
